  });




  return (


    <div className="exp-div">

      <button className="work-button" id="btn-brq" 
        onClick={ e => setWorkHistory({ 
          workName: x[0],
          workInfo: "Estágio em desenvolvimento de sistemas - 2019/2020",
          workDesc: "Criação de scripts para atividades diárias, incluindo automatizações de testes e alterações em massa para ambientes de programas COBOL. Construção de back-end JAVA para Intranet (busca e filtragem de dados para montagem de Relatórios Bancários). Criação de proc"
          })}>
            
