      <button className="work-button" id="btn-icesp">
        ICESP
        </button>

      <h2>{workHistory.workName}</h2>
      <p>{workHistory.workInfo}</p>
      <p>{workHistory.workDesc}</p>

    </div>

  )



}


export default Exp;
